dataview-inline-value.css:
.dataview-inline-value {
  color: mediumpurple;
  font-size: .95rem;
  font-weight: 400;
  line-height: 1em;
  text-align: center;
  text-decoration: none;
  margin: 0px 0px;
  border: 1px solid transparent;
  border-radius: 0.4em;
  transition: 150ms cubic-bezier(0.2, 0, 1, 0.9);
  filter: saturate(1);
}

dataview-inline-key.css:
.dataview-inline-key {
  color: mediumslateblue;
  font-size: .95rem;
  font-weight: 400;
  line-height: 1em;
  white-space: nowrap;
  text-overflow: ellipsis;
  overflow: hidden;
}
